"use client";

import { useEffect } from 'react';

export function ScreenshotProtection() {
  useEffect(() => {
    // Multi-layer protection against screenshots and content theft

    // Layer 1: Visual obscuring for screenshot attempts
    const obscureContent = () => {
      const overlay = document.createElement('div');
      overlay.id = 'screenshot-blocker';
      overlay.style.cssText = `
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: white;
        z-index: 999999;
        pointer-events: none;
      `;
      document.body.appendChild(overlay);

      setTimeout(() => {
        const existingOverlay = document.getElementById('screenshot-blocker');
        if (existingOverlay) {
          existingOverlay.remove();
        }
      }, 100);
    };

    // Layer 2: Body-level event protection
    const handleContextMenu = (e: Event) => {
      e.preventDefault();
      e.stopPropagation();
      return false;
    };

    const handleCopy = (e: Event) => {
      e.preventDefault();
      e.stopPropagation();
      return false;
    };

    const handleCut = (e: Event) => {
      e.preventDefault();
      e.stopPropagation();
      return false;
    };

    const handlePaste = (e: Event) => {
      e.preventDefault();
      e.stopPropagation();
      return false;
    };

    const handleDragStart = (e: Event) => {
      e.preventDefault();
      e.stopPropagation();
      return false;
    };

    const handleDrop = (e: Event) => {
      e.preventDefault();
      e.stopPropagation();
      return false;
    };

    // Layer 3: Keyboard shortcuts blocking
    const handleKeydown = (e: KeyboardEvent) => {
      // Screenshot shortcuts (all platforms)
      if (
        e.key === 'PrintScreen' ||
        e.key === 'Print' ||
        (e.metaKey && e.shiftKey && (e.key === '3' || e.key === '4' || e.key === '5')) || // macOS
        (e.ctrlKey && e.key === 'PrintScreen') || // Windows
        (e.shiftKey && e.key === 'PrintScreen') || // Windows Alt
        (e.ctrlKey && e.shiftKey && e.key === 'S') || // Custom screenshot tools
        (e.metaKey && e.shiftKey && e.key === 's') // macOS screenshot tools
      ) {
        e.preventDefault();
        e.stopPropagation();
        obscureContent();
        return false;
      }

      // Developer tools
      if (
        (e.ctrlKey && e.shiftKey && (e.key === 'I' || e.key === 'i')) ||
        (e.ctrlKey && e.shiftKey && (e.key === 'J' || e.key === 'j')) ||
        (e.ctrlKey && (e.key === 'U' || e.key === 'u')) ||
        e.key === 'F12'
      ) {
        e.preventDefault();
        e.stopPropagation();
        return false;
      }

      // Copy, save, select all shortcuts
      if (e.ctrlKey || e.metaKey) {
        if (
          e.key === 'c' || e.key === 'C' ||
          e.key === 's' || e.key === 'S' ||
          e.key === 'x' || e.key === 'X' ||
          e.key === 'a' || e.key === 'A'
        ) {
          // Allow in input fields only
          const target = e.target as HTMLElement;
          if (!target.matches('input, textarea, [contenteditable]')) {
            e.preventDefault();
            e.stopPropagation();
            return false;
          }
        }
      }
    };

    // Layer 4: Visibility change detection
    const handleVisibilityChange = () => {
      if (document.hidden) {
        obscureContent();
      }
    };

    // Layer 5: Page blur detection
    const handleBlur = () => {
      obscureContent();
    };

    // Layer 6: Mobile-specific screenshot detection
    const detectMobileScreenshot = () => {
      if (/Android|iPhone|iPad/i.test(navigator.userAgent)) {
        const observer = new MutationObserver(() => {
          if (document.hidden) {
            obscureContent();
          }
        });
        observer.observe(document.body, { attributes: true, childList: true, subtree: true });
        return observer;
      }
      return null;
    };

    // Layer 7: Apply protection to body element
    document.body.setAttribute('oncontextmenu', 'return false');
    document.body.style.userSelect = 'none';
    document.body.style.webkitUserSelect = 'none';
    document.body.style.webkitTouchCallout = 'none';

    // Register all event listeners
    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('blur', handleBlur);
    document.addEventListener('contextmenu', handleContextMenu);
    document.addEventListener('keydown', handleKeydown);
    document.addEventListener('copy', handleCopy);
    document.addEventListener('cut', handleCut);
    document.addEventListener('paste', handlePaste);
    document.addEventListener('dragstart', handleDragStart);
    document.addEventListener('drop', handleDrop);

    // Mobile screenshot detection
    const mobileObserver = detectMobileScreenshot();

    // Cleanup
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('blur', handleBlur);
      document.removeEventListener('contextmenu', handleContextMenu);
      document.removeEventListener('keydown', handleKeydown);
      document.removeEventListener('copy', handleCopy);
      document.removeEventListener('cut', handleCut);
      document.removeEventListener('paste', handlePaste);
      document.removeEventListener('dragstart', handleDragStart);
      document.removeEventListener('drop', handleDrop);
      if (mobileObserver) mobileObserver.disconnect();
    };
  }, []);

  return null;
}

src/utils/rate-limiting.ts.content:
// Rate limiting utilities for API protection and scalability

interface RateLimitConfig {
  windowMs: number;
  maxRequests: number;
  message?: string;
  skipSuccessfulRequests?: boolean;
}

/**
 * In-memory rate limiter for development and small scale
 */
export class InMemoryRateLimiter {
  private requests = new Map<string, number[]>();
  private config: RateLimitConfig;

  constructor(config: RateLimitConfig) {
    this.config = config;

    // Clean up old requests every minute
    setInterval(() => this.cleanup(), 60000);
  }

  /**
   * Check if request is allowed
   */
  isAllowed(identifier: string): boolean {
    const now = Date.now();
    const windowStart = now - this.config.windowMs;

    // Get existing requests for this identifier
    const userRequests = this.requests.get(identifier) || [];

    // Filter out requests outside the current window
    const validRequests = userRequests.filter(timestamp => timestamp > windowStart);

    // Check if user has exceeded the limit
    if (validRequests.length >= this.config.maxRequests) {
      return false;
    }

    // Add current request
    validRequests.push(now);
    this.requests.set(identifier, validRequests);

    return true;
  }

  /**
   * Get remaining requests for identifier
   */
  getRemaining(identifier: string): number {
    const now = Date.now();
    const windowStart = now - this.config.windowMs;
    const userRequests = this.requests.get(identifier) || [];
    const validRequests = userRequests.filter(timestamp => timestamp > windowStart);

    return Math.max(0, this.config.maxRequests - validRequests.length);
  }

  /**
   * Get time until reset
   */
  getResetTime(identifier: string): number {
    const userRequests = this.requests.get(identifier) || [];
    if (userRequests.length === 0) return 0;

    const oldestRequest = Math.min(...userRequests);
    return Math.max(0, oldestRequest + this.config.windowMs - Date.now());
  }

  /**
   * Clean up old requests
   */
  private cleanup(): void {
    const now = Date.now();
    const windowStart = now - this.config.windowMs;

    for (const [identifier, requests] of this.requests.entries()) {
      const validRequests = requests.filter(timestamp => timestamp > windowStart);

      if (validRequests.length === 0) {
        this.requests.delete(identifier);
      } else {
        this.requests.set(identifier, validRequests);
      }
    }
  }

  /**
   * Get statistics
   */
  getStats() {
    return {
      totalIdentifiers: this.requests.size,
      config: this.config
    };
  }
}

/**
 * Rate limit configurations for different endpoints
 */
export const RATE_LIMIT_CONFIGS = {
  // API endpoints
  api: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 100, // 100 requests per 15 minutes
    message: 'Too many API requests, please try again later.'
  },

  // Chat endpoints (more restrictive)
  chat: {
    windowMs: 1 * 60 * 1000, // 1 minute
    maxRequests: 20, // 20 messages per minute
    message: 'Too many chat messages, please slow down.'
  },

  // Auth endpoints (very restrictive)
  auth: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    maxRequests: 5, // 5 attempts per 15 minutes
    message: 'Too many authentication attempts, please try again later.'
  },

  // General endpoints
  general: {
    windowMs: 1 * 60 * 1000, // 1 minute
    maxRequests: 60, // 60 requests per minute
    message: 'Rate limit exceeded, please try again later.'
  }
};

/**
 * Rate limiter instances
 */
export const rateLimiters = {
  api: new InMemoryRateLimiter(RATE_LIMIT_CONFIGS.api),
  chat: new InMemoryRateLimiter(RATE_LIMIT_CONFIGS.chat),
  auth: new InMemoryRateLimiter(RATE_LIMIT_CONFIGS.auth),
  general: new InMemoryRateLimiter(RATE_LIMIT_CONFIGS.general)
};

/**
 * Helper function to get client identifier
 */
export function getClientIdentifier(req: any): string {
  // Try to get real IP from various headers
  const forwarded = req.headers['x-forwarded-for'];
  const realIp = req.headers['x-real-ip'];
  const clientIp = req.headers['x-client-ip'];

  let ip = forwarded || realIp || clientIp || req.connection?.remoteAddress || 'unknown';

  // Handle comma-separated IPs (from proxies)
  if (typeof ip === 'string' && ip.includes(',')) {
    ip = ip.split(',')[0].trim();
  }

  return ip as string;
}

/**
 * Rate limiting middleware function
 */
export function createRateLimitMiddleware(limiterType: keyof typeof rateLimiters) {
  return (req: any, res: any, next: () => void) => {
    const identifier = getClientIdentifier(req);
    const limiter = rateLimiters[limiterType];

    if (!limiter.isAllowed(identifier)) {
      const config = RATE_LIMIT_CONFIGS[limiterType];
      res.status(429).json({
        error: config.message,
        retryAfter: Math.ceil(limiter.getResetTime(identifier) / 1000)
      });
      return;
    }

    // Add rate limit headers
    const rateLimitConfig = RATE_LIMIT_CONFIGS[limiterType];
    res.setHeader('X-RateLimit-Limit', rateLimitConfig.maxRequests);
    res.setHeader('X-RateLimit-Remaining', limiter.getRemaining(identifier));
    res.setHeader('X-RateLimit-Reset', Math.ceil((Date.now() + limiter.getResetTime(identifier)) / 1000));

    next();
  };
}

/**
 * Distributed rate limiting (for production with Redis)
 */
export class DistributedRateLimiter {
  // This would integrate with Redis in production
  // For now, we'll use the in-memory limiter as fallback

  constructor(private config: RateLimitConfig) {}

  async isAllowed(identifier: string): Promise<boolean> {
    // In production, this would use Redis
    // For now, fallback to in-memory
    const limiter = new InMemoryRateLimiter(this.config);
    return limiter.isAllowed(identifier);
  }

  async getRemaining(identifier: string): Promise<number> {
    // Redis implementation would go here
    const limiter = new InMemoryRateLimiter(this.config);
    return limiter.getRemaining(identifier);
  }
}

src/app/api/analytics/batch/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { supabase } from '@/lib/supabaseClient';
import MaximumSecurity from '@/lib/enhanced-security';
import { APISecurityManager } from '@/lib/api-security';

interface BatchAnalyticsEvent {
  eventType: string;
  eventData: any;
  userId?: string;
  sessionId: string;
  timestamp: number;
}

export async function POST(request: NextRequest) {
  // Apply MAXIMUM security protection
  const enhancedSecurityCheck = await MaximumSecurity.secureRequest(request);
  if (enhancedSecurityCheck) return enhancedSecurityCheck;

  // Apply API security with rate limiting
  const securityCheck = await APISecurityManager.secureAPIRoute(request, {
    allowedMethods: ['POST'],
    rateLimit: { requests: 200, window: 60000 } // 200 requests per minute for batch analytics
  });
  if (securityCheck) return securityCheck;

  try {
    const body = await request.json();

    // Validate and sanitize POST data
    const postValidation = await MaximumSecurity.validatePostData(request, body);
    if (!postValidation.valid) {
      return NextResponse.json(
        { error: postValidation.error || 'Invalid request' },
        { status: 400 }
      );
    }

    const { events, sessionId } = postValidation.sanitized;

    if (!Array.isArray(events) || events.length === 0) {
      return NextResponse.json({ error: 'No events provided' }, { status: 400 });
    }

    // Limit batch size to prevent abuse
    if (events.length > 100) {
      return NextResponse.json({ error: 'Batch size too large. Maximum 100 events per request.' }, { status: 400 });
    }

    const results = await Promise.allSettled(
      events.map((event: BatchAnalyticsEvent) => processEvent(event))
    );

    const successful = results.filter(r => r.status === 'fulfilled').length;
    const failed = results.filter(r => r.status === 'rejected').length;

    return NextResponse.json({
      success: true,
      processed: events.length,
      successful,
      failed,
      sessionId
    });

  } catch (error: any) {
    console.error('Batch Analytics Error:', error);
    return NextResponse.json(
      { error: 'Failed to process batch events', details: error.message },
      { status: 500 }
    );
  }
}

async function processEvent(event: BatchAnalyticsEvent) {
  const { eventType, eventData, userId, sessionId, timestamp } = event;
  const chatId = eventData.chatId || 'kruthika_chat';

  try {
    switch (eventType) {
      case 'message_sent':
        // Insert message log
        const { error: msgError } = await supabase
          .from('messages_log')
          .insert({
            message_id: eventData.messageId || `msg_${timestamp}`,
            sender_type: eventData.senderType || 'user',
            chat_id: chatId,
            text_content: eventData.content?.substring(0, 500),
            has_image: eventData.hasImage || false,
            created_at: new Date(timestamp).toISOString()
          });

        if (msgError) throw msgError;

        // Update daily activity
        const today = new Date(timestamp).toISOString().split('T')[0];
        const userPseudoId = userId || sessionId || 'anonymous_' + timestamp;

        await supabase
          .from('daily_activity_log')
          .upsert({
            user_pseudo_id: userPseudoId,
            activity_date: today,
            chat_id: chatId
          }, {
            onConflict: 'user_pseudo_id,activity_date,chat_id',
            ignoreDuplicates: true
          });

        // Update daily analytics aggregation
        await updateDailyAnalytics(today, eventData.senderType === 'user' ? 1 : 0);
        break;

      case 'session_start':
      case 'session_resume':
        const sessionDate = new Date(timestamp).toISOString().split('T')[0];
        const sessionUserId = userId || sessionId || 'anonymous_' + timestamp;

        await supabase
          .from('daily_activity_log')
          .upsert({
            user_pseudo_id: sessionUserId,
            activity_date: sessionDate,
            chat_id: chatId
          }, {
            onConflict: 'user_pseudo_id,activity_date,chat_id',
            ignoreDuplicates: true
          });
        break;

      case 'ad_interaction':
        const adDate = new Date(timestamp).toISOString().split('T')[0];
        const impressions = eventData.action === 'view' ? 1 : 0;
        const clicks = eventData.action === 'click' ? 1 : 0;
        const revenue = clicks * 0.01 + impressions * 0.001; // Estimate

        await supabase
          .from('ad_revenue_log')
          .upsert({
            date: adDate,
            ad_network: eventData.network || 'unknown',
            ad_type: eventData.adType || 'unknown',
            impressions: impressions,
            clicks: clicks,
            estimated_revenue: revenue,
            cpm: revenue / Math.max(impressions, 1) * 1000
          }, {
            onConflict: 'date,ad_network,ad_type'
          });
        break;

      case 'image_shared':
        // Track in messages_log with has_image flag
        await supabase
          .from('messages_log')
          .insert({
            message_id: `img_${timestamp}`,
            sender_type: 'user',
            chat_id: chatId,
            text_content: 'Image shared',
            has_image: true,
            created_at: new Date(timestamp).toISOString()
          });
        break;

      case 'page_view':
        // Could track page views in a separate table if needed
        console.log('Page view tracked:', eventData.page);
        break;

      case 'user_action':
        // Log user actions for behavior analysis
        if (eventData.action === 'performance_metric') {
          await updateDailyAnalytics(
            new Date(timestamp).toISOString().split('T')[0], 
            0, 
            0, 
            eventData.details?.duration || 0
          );
        }
        break;

      default:
        console.warn('Unknown batch event type:', eventType);
    }

    return { success: true, eventType };
  } catch (error: any) {
    console.error(`Error processing ${eventType}:`, error);
    throw error;
  }
}

async function updateDailyAnalytics(date: string, messageCount: number = 0, errorCount: number = 0, responseTime: number = 0) {
  try {
    // Get current DAU for the date
    const { data: dauData } = await supabase
      .from('daily_activity_log')
      .select('user_pseudo_id')
      .eq('activity_date', date);

    const dau = dauData ? new Set(dauData.map(d => d.user_pseudo_id)).size : 0;

    // Upsert daily analytics
    await supabase
      .from('daily_analytics')
      .upsert({
        date: date,
        dau: dau,
        message_count: messageCount,
        error_count: errorCount,
        response_time: responseTime,
        updated_at: new Date().toISOString()
      }, {
        onConflict: 'date'
      });

  } catch (error) {
    console.error('Error updating daily analytics:', error);
  }
}

