export interface ImageSharingContext {
  userMessage: string;
  recentMessages: string[];
  relationshipLevel: number; // 0-1
  timeOfDay: 'morning' | 'afternoon' | 'evening' | 'night';
  userLanguage: string;
  chatTone: 'casual' | 'flirty' | 'romantic' | 'friendly';
