  }, [
    messages,
    currentAiNameForOfflineMsg,
    recentInteractions,
    isLoadingChatState,
    toast,
    isLoadingAdSettings,
    isLoadingAIProfile,
    isLoadingMediaAssets,
    adSettings,
  ]);

  const onlineStatus = useMemo(() => {
