    const dailyCount = typeof window !== "undefined" 
      ? parseInt(localStorage.getItem(USER_DAILY_MESSAGE_COUNT_KEY) || "0")
      : 0;
    const isNewUser = dailyCount <= 20;

    // Add realistic activity status
    const getCurrentActivityStatus = (): string => {
      const now = new Date();
