      const dayOfWeek = now.getDay();

      // Weekend activities
      if (dayOfWeek === 0 || dayOfWeek === 6) {
        if (istHour >= 10 && istHour <= 12) return "getting ready to go out ðŸ’„";
        if (istHour >= 13 && istHour <= 16)
          return "shopping at linking road ðŸ›ï¸";
