"use client";

import React, { useState, useEffect, useRef } from 'react';
import { useAdSettings } from '@/contexts/AdSettingsContext';
import { cn } from '@/lib/utils';

const SocialBarAdDisplay: React.FC = () => {
  const { adSettings, isLoadingAdSettings } = useAdSettings();
  const [isVisible, setIsVisible] = useState(false);
  const [adCodeToInject, setAdCodeToInject] = useState<string | null>(null);
  const adContainerRef = useRef<HTMLDivElement>(null);
  const scriptInjectedRef = useRef(false);

  useEffect(() => {
    if (isLoadingAdSettings || !adSettings || !adSettings.adsEnabledGlobally) {
      setIsVisible(false);
      setAdCodeToInject(null);
      scriptInjectedRef.current = false;
      return;
    }

    let selectedAdCode = "";

    if (adSettings.adsterraSocialBarEnabled && adSettings.adsterraSocialBarCode) {
      selectedAdCode = adSettings.adsterraSocialBarCode;
    } else if (adSettings.monetagSocialBarEnabled && adSettings.monetagSocialBarCode) {
      selectedAdCode = adSettings.monetagSocialBarCode;
    }

    if (selectedAdCode.trim()) {
      setAdCodeToInject(selectedAdCode);
      setIsVisible(true);
    } else {
      setAdCodeToInject(null);
      setIsVisible(false);
      scriptInjectedRef.current = false;
    }
  }, [adSettings, isLoadingAdSettings]);

  useEffect(() => {
    if (adCodeToInject && adContainerRef.current && !scriptInjectedRef.current) {
      adContainerRef.current.innerHTML = '';

      try {
        const fragment = document.createRange().createContextualFragment(adCodeToInject);
        adContainerRef.current.appendChild(fragment);
        scriptInjectedRef.current = true;
      } catch (e) {
        console.error("Error injecting social bar ad:", e);
      }
    }
  }, [adCodeToInject]);

  if (isLoadingAdSettings || !isVisible || !adCodeToInject) {
    return null;
  }

  return (
    <div
      ref={adContainerRef}
      className={cn(
        "!fixed !bottom-0 !left-0 !right-0",
        "!z-[95] !w-full !max-h-[120px]",
        "flex justify-center items-end overflow-hidden"
      )}
      style={{
        bottom: '0px',
        left: '0px',
        right: '0px',
        position: 'fixed',
        zIndex: 95,
        maxHeight: '120px'
      }}
    />
  );
};

export default SocialBarAdDisplay;

src/components/CookieConsent.tsx.content:
"use client";

import React, { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Cookie, Settings } from 'lucide-react';
import Link from 'next/link';
import { cn } from '@/lib/utils';

const COOKIE_CONSENT_KEY = 'kruthika_cookie_consent_v2';
const COOKIE_CONSENT_EXPIRY = 365; // days

interface CookieConsentProps {
  className?: string;
}

export const CookieConsent: React.FC<CookieConsentProps> = ({ className }) => {
  const [showConsent, setShowConsent] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [showCustomize, setShowCustomize] = useState(false);

  useEffect(() => {
    // Only run in browser
    if (typeof window === 'undefined') return;

    // Small delay to ensure DOM is ready and avoid hydration issues
    const checkConsent = () => {
      try {
        // Check localStorage first
        const localStorageConsent = localStorage.getItem(COOKIE_CONSENT_KEY);

        // Check if cookie exists
        const cookieExists = document.cookie
          .split(';')
          .some(item => item.trim().startsWith(COOKIE_CONSENT_KEY + '='));

        // Only show consent if BOTH checks are false (new user)
        const hasNoConsent = !localStorageConsent && !cookieExists;

        if (hasNoConsent) {
          console.log('ðŸª New user detected - showing cookie consent');
          setShowConsent(true);
          // Use setTimeout to ensure smooth animation
          setTimeout(() => setIsVisible(true), 50);
        } else {
          console.log('ðŸª Existing user - consent already given');
        }
      } catch (error) {
        console.error('Cookie consent check error:', error);
        // On error, show consent to be safe
        setShowConsent(true);
        setTimeout(() => setIsVisible(true), 50);
      }
    };

    // Run check after component mount
    checkConsent();
  }, []);

  const setCookieConsent = (preferences: {
    necessary: boolean;
    analytics: boolean;
    advertising: boolean;
    personalization: boolean;
    aiLearning?: boolean;
    intimacyLevel?: boolean;
  }) => {
    try {
      const consentData = {
        ...preferences,
        timestamp: new Date().toISOString(),
        version: 'v2'
      };

      // Save to localStorage
      localStorage.setItem(COOKIE_CONSENT_KEY, JSON.stringify(consentData));

      // Set cookie with expiry
      const expiryDate = new Date();
      expiryDate.setDate(expiryDate.getDate() + COOKIE_CONSENT_EXPIRY);
      document.cookie = `${COOKIE_CONSENT_KEY}=true; expires=${expiryDate.toUTCString()}; path=/; SameSite=Lax`;

      console.log('ðŸª Cookie consent saved:', preferences);

      // Hide the consent banner
      hideConsent();
    } catch (error) {
      console.error('Error saving cookie consent:', error);
    }
  };

  const handleAcceptAll = () => {
    setCookieConsent({
      necessary: true,
      analytics: true,
      advertising: true,
      personalization: true,
      aiLearning: true,
      intimacyLevel: true
    });
  };

  const handleAcceptNecessary = () => {
    setCookieConsent({
      necessary: true,
      analytics: false,
      advertising: false,
      personalization: false,
      aiLearning: false,
      intimacyLevel: false
    });
  };

  const hideConsent = () => {
    setIsVisible(false);
    setTimeout(() => {
      setShowConsent(false);
    }, 300);
  };

  const toggleCustomize = () => {
    setShowCustomize(!showCustomize);
  };

  // Fixed CLS implementation - no body padding, smooth slide-in
  return (
    <>
      {showConsent && (
        <div
          className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur-sm border-t border-gray-200 shadow-lg z-[9999]"
          style={{
            transform: isVisible ? 'translateY(0)' : 'translateY(100%)',
            transition: 'transform 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
            willChange: 'transform',
            contain: 'layout size style'
          }}
        >
          <div
            className={cn(
              "max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 h-full pointer-events-auto",
              className
            )}
          >
            <Card className={cn(
              "w-full max-w-md mx-auto pointer-events-auto",
              "bg-white/95 backdrop-blur-xl border border-gray-200/50 shadow-2xl rounded-2xl",
              "transform transition-all duration-300",
              "max-h-[90vh] overflow-y-auto", // More responsive height
              showCustomize ? "pb-2" : "pb-1" // Add bottom padding when expanded
            )}>
              <CardContent className="p-4 space-y-3 sm:p-5 sm:space-y-4">
                {/* Compact Header */}
                <div className="flex items-start gap-3">
                  <div className="flex-shrink-0 p-2 bg-[#25d366]/10 rounded-lg">
                    <Cookie className="h-5 w-5 text-[#25d366]" />
                  </div>
                  <div className="flex-1 min-w-0">
                    <h2 className="font-semibold text-gray-900 text-sm mb-1">Cookie Settings</h2>
                    <p className="text-xs text-gray-700 leading-relaxed">
                      We use cookies to make Kruthika remember you better and show relevant content.
                      <span className="font-medium text-[#25d366]">Accept all for the best experience!</span>
                      <Link href="/legal/privacy" className="text-[#25d366] hover:underline ml-1 font-semibold underline">
                        Privacy Policy details
                      </Link>
                    </p>
                  </div>
                </div>

                {/* Customize Panel */}
                {showCustomize && (
                  <div className="space-y-3 border-t border-gray-100 pt-3 animate-in slide-in-from-top-2 duration-200">
                    <div className="space-y-2">
                      {[
                        { name: "Essential", desc: "Required for basic functionality", required: true },
                        { name: "Analytics", desc: "Help us improve our service" },
                        { name: "Personalization", desc: "Customize your experience" },
                        { name: "Marketing", desc: "Show relevant content" }
                      ].map((item, idx) => (
                        <div key={idx} className="flex justify-between items-center py-1">
                          <div>
                            <span className="text-xs font-medium text-gray-700">{item.name}</span>
                            <p className="text-xs text-gray-500">{item.desc}</p>
                          </div>
                          {item.required ? (
                            <span className="text-xs bg-[#25d366]/10 text-[#25d366] px-2 py-1 rounded-full">
                              Required
                            </span>
                          ) : (
                            <div className="w-8 h-4 bg-[#25d366] rounded-full relative">
                              <div className="w-3 h-3 bg-white rounded-full absolute top-0.5 right-0.5"></div>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Action Buttons */}
                <div className="flex flex-col gap-2">
                  <div className="flex gap-2">
                    <Button
                      onClick={handleAcceptAll}
                      className="flex-1 bg-[#25d366] hover:bg-[#1faa55] text-white text-sm py-2 h-10 font-medium shadow-sm"
                      aria-label="Accept all cookies and tracking"
                    >
                      Accept All
                    </Button>
                    <Button
                      onClick={toggleCustomize}
                      variant="outline"
                      className="flex-shrink-0 border-gray-300 text-gray-600 hover:bg-gray-50 text-sm py-2 h-10 px-3"
                      aria-label="Customize cookie preferences"
                    >
                      <Settings className="h-4 w-4" />
                    </Button>
                  </div>

                  <div className="flex gap-2">
                    <Button
                      onClick={handleAcceptNecessary}
                      variant="ghost"
                      className="flex-1 text-gray-600 hover:bg-gray-100 text-xs py-2 h-9"
                      aria-label="Accept essential cookies only"
                    >
                      Essential Only
                    </Button>
                    {showCustomize && (
                      <Button
                        onClick={handleAcceptAll}
                        className="flex-1 bg-[#25d366] hover:bg-[#1faa55] text-white text-xs py-2 h-9"
                        aria-label="Save cookie preferences"
                      >
                        Save Preferences
                      </Button>
                    )}
                  </div>
                </div>

                {/* Legal Links */}
                <div className="text-center">
                  <p className="text-xs text-gray-700">
                    By continuing, you agree to our{' '}
                    <Link href="/legal/terms" className="text-[#25d366] hover:underline font-semibold underline">
                      Terms of Service
                    </Link>{' '}
                    and{' '}
                    <Link href="/legal/privacy" className="text-[#25d366] hover:underline font-semibold underline">
                      Privacy Policy
                    </Link>
                  </p>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      )}
    </>
  );
};

export default CookieConsent;

